/* eslint-disable no-labels */
/* eslint-disable no-template-curly-in-string */
import React, { useState } from "react";

const Todo = ({ status, text, todo, todos, setTodos }) => {
  const inputTextHandler = (e) => {
    console.log(e);
    setInputText(e.target.value);
  };
  const [inputText, setInputText] = useState("");
  const editHandler = () => {
    return (
      <input
        value={inputText}
        onChange={inputTextHandler}
        type="text"
        className="todo-input"
      />
    );
  };
  let edit = setTodos(
    todos.map((item) => {
      if (item.id === todo.id) {
        return (
          <input type="text" value={inputText}>
            {}text\\\\
          </input>
        );
      }
    })
  );

  const deleteHandler = () => {
    setTodos(
      todos.map((item) => {
        if (item.id === todo.id) {
          if (todo.status === "removed") {
            return {
              ...item,
              status: "asset",
            };
          } else {
            return {
              ...item,
              status: "removed",
            };
          }
        }
        return item;
      })
    );
  };

  const completeHandler = () => {
    setTodos(
      todos.map((item) => {
        if (item.id === todo.id) {
          return {
            ...item,
            completed: !item.completed,
          };
        }
        return item;
      })
    );
  };

  return (
    <div className="todo">
      <li className={`todo-item ${todo.completed ? "completed" : ""}`}>
        {text}
      </li>
      {/* <Router>
        <Link to="/removed"><button onClick={editHandler} className="complete-btn"><i className="fas fa-check"></i></button></Link>
        <Switch>
        <Route path="/removed">
              <Edit
                setTodos={setTodos}
                todos={todos}
              />
            </Route>
          </Switch>
        </Router> */}

      <button onClick={editHandler} className="complete-btn">
        <i className="fas fa-check">Edit</i>
      </button>
      <button onClick={completeHandler} className="complete-btn">
        <i className="fas fa-check"></i>
      </button>
      <button onClick={deleteHandler} className="trash-btn">
        <i className="fas fa-trash">{status}</i>
      </button>
    </div>
  );
};
export default Todo;
