/* eslint-disable no-template-curly-in-string */
import React from "react";

const Todo = ({ status, text, todo, todos, setTodos, setInputText, inputText }) => {
  const editHandler = ({text}) => {
    console.log({text});
      alert(text);
      // setInputText({text})
    // let item = text;
    // setInputText(item);
    setInputText(inputText);

    setTodos(todos.filter((el) => el.id !== todo.id));
  };

  const deleteHandler = () => {
    setTodos(
      todos.map((item) => {
        if (item.id === todo.id) {
          if (todo.status === "removed") {
            return {
              ...item,
              status: "asset",
            };
          } else {
            return {
              ...item,
              status: "removed",
            };
          }
        }
        return item;
      })
    );
  };

  const completeHandler = () => {
    setTodos(
      todos.map((item) => {
        if (item.id === todo.id) {
          return {
            ...item,
            completed: !item.completed,
          };
        }
        return item;
      })
    );
  };

  return (
    <div className="todo">
      <li className={`todo-item ${todo.completed ? "completed" : ""}`}>
        {text}
      </li>
      <button onClick={()=>editHandler()} className="complete-btn">
        <i className="fas fa-check">Edit</i>
      </button>
      <button onClick={completeHandler} className="complete-btn">
        <i className="fas fa-check"></i>
      </button>
      <button onClick={deleteHandler} className="trash-btn">
        <i className="fas fa-trash">{status}</i>
      </button>
    </div>
  );
};
export default Todo;
