import React from "react";
import TodoNav from "./TodoNav";

const Navbar = ({ todos, setTodos, filteredTodos }) => {
  setTodos(
    todos.map((item) => {
      if (item.id === todo.id) {
        return {
          ...item,
          completed: !item.completed,
        };
      }
      return item;
    })
  );
  let elements = setTodos.map((item) =>{
    let result='';
    if(item.completed){
      result = <ul className="todo-list">
      {filteredTodos.map((todo) => (
        <TodoNav
          key={todo.id}
          text={todo.text}
          setTodos={setTodos}
          todos={todos}
          todo={todo}
        />
      ))}
    </ul>
    }
    return result;
  })
  return (
    <div className="todo-container">
    {elements}
  </div>
  );
}

export default Navbar;
