/* eslint-disable no-labels */
/* eslint-disable no-template-curly-in-string */
import React,{ useState} from "react";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

const Todo = ({ status, text, todo, todos, setTodos}) => {
  const [inputText, setInputText] = useState("");
  const editHandler = () => {
      todos.map((item) => {
        if (item.id === todo.id) {
          setInputText("aaa")
          return <input type="text" value={inputText}>{text}</input>
        }
      })
    
  }


  const deleteHandler = () => {
    setTodos(
      todos.map((item) => {
        if (item.id === todo.id) {
          if (todo.status === "removed") {
            return {
              ...item,
              status: "asset",
            };
          } else {
            return {
              ...item,
              status: "removed",
            };
          }
        }
        return item;
      })
    );
  };

  const completeHandler = () => {
    setTodos(
      todos.map((item) => {
        if (item.id === todo.id) {
          return {
            ...item,
            completed: !item.completed,
          };
        }
        return item;
      })
    );
  };

  return (
    <div className="todo">
      <li className={`todo-item ${todo.completed ? "completed" : ""}`}>
        {text}
      </li>
      <Router>
        <Link to="/removed"><button onClick={editHandler} className="complete-btn"><i className="fas fa-check"></i></button></Link>
        <Switch>
        <Route path="/removed">
              <Edit
                setTodos={setTodos}
                todos={todos}
              />
            </Route>
          </Switch>
        </Router>
      <button onClick={completeHandler} className="complete-btn">
        <i className="fas fa-check"></i>
      </button>
      <button onClick={deleteHandler} className="trash-btn">
        <i className="fas fa-trash">{status}</i>
      </button>
    </div>
  );
};
export default Todo;
